import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';

function App() {
  const initialValues = {firstName:"", lastName:"", age:""}
  const [formValue, setFormValue] = useState(initialValues)
  const [formErors,setFormErors] = useState({})

  const onInputChange = (e) => {
    const {name,value} = e.target;
    setFormValue({...formValue,[name]:value})
  }

  useEffect(() => {
    const timer = setTimeout(() => {
      console.log("in timer")
      setFormValue(formValue)
    }, 5000);
    return () => clearTimeout(timer);
  }, [formValue]);

  const validations = (value) => {
    const errors = {};
    if(value.firstName && value.firstName.length < 4) errors.firstName = "სახელი უნდა შეიცავდეს მინუმუ 4 ასოს";
    if(value.lastName && value.lastName.length < 4) errors.firstName = "გვარი უნდა შეიცავდეს მინიმუ 4 ასოს";
    if(value.age && value.age <= 18) errors.firstName = "ასაკი უნდა აღემატებოდეს 18 წელს ";
    return errors;
  }

  useEffect(()=>{
    setFormErors(validations(formValue))
  },[formValue])

  return (
    <div className="formValues">

      <form>

      </form>


    </div>
  );
}

export default App;


